mapper.select:
select count(*) from t_forward_number_list t left JOIN tb_ucpaas_city2 c ON t.areaid = c.cityid where 1=1
and c.city like CONCAT('%',#{city,jdbcType=VARCHAR},'%')
and t.isp = #{isp}
and t.forwardnumber like CONCAT('%',#{forwardnumber,jdbcType=VARCHAR},'%')
and t.type = #{type}
and t.status = #{status}

SELECT t.id, t.areaid, c.city, t.isp, t.multi, t.counter, t.forwardnumber, t.type, t.status, t.remark FROM t_forward_number_list t left JOIN tb_ucpaas_city2 c on t.areaid = c.cityid where 1=1
and c.city like CONCAT('%',#{city,jdbcType=VARCHAR},'%')
and t.isp = #{isp}
and t.forwardnumber like CONCAT('%',#{forwardnumber,jdbcType=VARCHAR},'%')
and t.type = #{type}
and t.status = #{status}
LIMIT #{begin,jdbcType=INTEGER} , #{count,jdbcType=INTEGER}

mapper.update:
update t_forward_number_list
set counter = counter - 1
where forwardnumber = #{0}
